import scrollToElement from"./scroll-to-element.min.js";import{toggleMobileNav}from"./header-mobile.min.js";const scrollToAbout=()=>{scrollToElement(".main-about")},scrollToResume=()=>{scrollToElement(".main-resume")},scrollToWork=()=>{scrollToElement(".main-work")},scrollToContact=()=>{scrollToElement(".main-contact")},activateHeaderListeners=()=>{document.querySelectorAll("nav li:first-child").forEach(e=>{e.addEventListener("click",()=>{scrollToAbout(),toggleMobileNav()}),e.addEventListener("keypress",e=>{13===(e.which||e.keyCode)&&(scrollToAbout(),toggleMobileNav())})}),document.querySelectorAll("nav li:nth-child(2)").forEach(e=>{e.addEventListener("click",()=>{scrollToResume(),toggleMobileNav()}),e.addEventListener("keypress",e=>{13===(e.which||e.keyCode)&&(scrollToResume(),toggleMobileNav())})}),document.querySelectorAll("nav li:nth-child(3)").forEach(e=>{e.addEventListener("click",()=>{scrollToWork(),toggleMobileNav()}),e.addEventListener("keypress",e=>{13===(e.which||e.keyCode)&&(scrollToWork(),toggleMobileNav())})}),document.querySelectorAll("nav li:last-child").forEach(e=>{e.addEventListener("click",()=>{scrollToContact(),toggleMobileNav()}),e.addEventListener("keypress",e=>{13===(e.which||e.keyCode)&&(scrollToContact(),toggleMobileNav())})})};export default activateHeaderListeners;